import { AxiosPromise } from "axios";
interface GetOptions {
    path: string;
    params?: unknown;
}
declare class PoeClient {
    client: import("axios").AxiosInstance;
    sessionId: string | undefined;
    get<T>({ path, params }: GetOptions): Promise<T>;
    resolve<T>(callback: () => AxiosPromise): Promise<T>;
}
declare const poeClient: PoeClient;
export default poeClient;
